import { TodoCounter } from './TodoCounter/TodoCounter';
import { TodoSearch } from './TodoSearch/TodoSearch';
import { TodoList } from './TodoList/TodoList';
import { TodoItem } from './TodoItem/TodoItem';
import { CreateTodoButton } from './CreateTodoButton/CreateTodoButton';
import React from 'react';

const defaultTodos = [
  { text: 'Cortar cebolla', completed: false },
  { text: 'Tomar el Curso de Intro a React.js', completed: false },
  { text: 'Llorar con la Llorona', completed: false },
  { text: 'LALALALA', completed: false }
]


function App() {
  const [todos, setTodos] = React.useState(defaultTodos)
  const [searchValue, setSearchValue] = React.useState('')
  const [deshacer, setDeshacer] = React.useState(1)

  const completedTodos = todos.filter(todo => !!todo.completed).length;
  const totalTodos = todos.length;

  const searchedTodos = todos.filter(
    (todo) => {
      const todoText = todo.text.toLocaleLowerCase()
      const searchText = searchValue.toLocaleLowerCase()  
      return todoText.includes(searchText)
    }
  )

  const completeTodo = (text) => {
    const newTodos = [...todos]
    const todoIndex = newTodos.findIndex(todo => todo.text === text) 
    newTodos[todoIndex].completed = !newTodos[todoIndex].completed
    setTodos(newTodos)
  }

  const deleteTodo = (text) => {
    const newTodos = [...todos]
    const todoIndex = newTodos.findIndex(todo => todo.text === text)
    newTodos.splice(todoIndex, 1)
    setTodos(newTodos)
    deshacerTodos()
  }

  const deshacerTodos = () => {
    const todosCopy = [...todos]

    while (deshacer !== 3) {
      let promptDeshacer = (prompt('Esta seguro que desea eliminar este TODO?\nPresione:\n1 Si desea eliminarlo\n2 Si desea conservarlo\n3 Para eliminarlo y que no aparezca mas este mensaje'))

      if(promptDeshacer == 1) {
        setDeshacer(1)
        break;
      } else if(promptDeshacer == 2) {
        setTodos(todosCopy)
        break;
      } else if(promptDeshacer == 3) {
        alert('Entendido no volveremos a mostrar este mensaje')
        setDeshacer(3)
        break;
      } else {
        setDeshacer(4)
      }
    }
  }

  const createTodo = () => {
    const newTodo = [...todos]
    const newTodoText = prompt('Ingrese el nombre del nuevo TODO')
    newTodo.push({text:newTodoText, completed})
  }

  return (
    <>
      <TodoCounter 
        completed={completedTodos}
        total={totalTodos} 
      />
      <TodoSearch 
        searchValue={searchValue}
        setSearchValue={setSearchValue}
      />

      <TodoList>
        {searchedTodos.map(todo => (
          <TodoItem 
            key={todo.text}
            text={todo.text} 
            completed={todo.completed}
            onComplete={completeTodo}
            onDelete={deleteTodo}
          />
        ))}
      </TodoList>

      <CreateTodoButton
        onClick={createTodo}
      />
    </>
  );
}

export default App;
