import { TodoSearch } from '../TodoSearch/TodoSearch';
import './TodoItem.css'

function TodoItem({ text, completed, todos, complete }) {
    return (
      <li className='TodoItem'>
        <span className={`Icon Icon-check ${completed && 'Icon-check--active'}`} 
              onClick={() => {completarTodo(setTodos, todos, text)}}>
          V
        </span>
        <p className={`TodoItem-p ${completed && 'TodoItem-p--complete'}`}>
          {text}
        </p>
        <span className='Icon Icon-delete'>
          X
        </span>
      </li>
    );
}

function completarTodo(setTodos, todos, text) {
  console.log('asa')
  const newTodo = todos
  console.log(newTodo)

  newTodo.forEach(todo => {
    if(todo.text === text) {
      todo.completed = true
    }
  });

  setTodos(newTodo)
}

export { TodoItem };
